import {Injectable} from '@angular/core';
import {Recipe} from './recipe.model';
import {Ingredient} from '../shared/Ingredient.model';
import {ShoppingListService} from '../shopping-list/shopping-list.service';
import Swal from 'sweetalert2';

@Injectable()
export class RecipeService {


    private recipes: Recipe[] = [
        new Recipe(1, 'Tagine', 'Moroccan Tagine With Meat', 'data:image/jpeg;base64,', [
            new Ingredient(1, 'Chicken', 2),
            new Ingredient(2, 'potatos', 5)
        ]),
        new Recipe(2, 'Oat meal', 'Oatmeal is a type of coarse flour made of hulled oat grains – groats – that have either been milled, steel-cut, or rolled. Ground oats are also called "white oats". Steel-cut oats are known as "coarse oatmeal" or "Irish oatmeal" or "pinhead oats"', 'data:image/jpeg;base64,', [
            new Ingredient(3, 'Oats', 2),
            new Ingredient(4, 'cheese', 2)
        ]),
        new Recipe(3, 'Chicken', 'The chicken is a type of domesticated fowl, a subspecies of the red junglefowl. It is one of the most common and widespread domestic animals, with a total population of more than 19 billion as of 2011. There are more chickens in the world than any other bird or domesticated fowl. Wikipedia', 'data:image/jpeg;base64,', [
            new Ingredient(5, 'chicken', 2),
            new Ingredient(6, 'Onion', 2)
        ])
    ];

    constructor(private shoppingListService: ShoppingListService) {
    }

    getRecipe(id: number) {
        return this.recipes.find(recipe => recipe.id == id);
    }

    saveRecipe(recipe: Recipe) {
        if (this.recipes.filter(tempRecipe => tempRecipe.id == recipe.id).length > 0) {
            this.recipes.push(recipe);
        }
    }

    getRecipes() {
        return this.recipes.slice();
    }

    addIngredientsToShoppingList(ingredients: Ingredient[]) {
        this.shoppingListService.saveIngredients(ingredients);
        Swal.fire(
            'Good job!',
            'Ingredients added to shopping list successflly',
            'success'
        );
    }

    updateRecipe(param: { id: number; recipeDetails: { recipeName: string; recipeDescription: string; recipeImagePath: string; recipeIngredients: Ingredient[] } }) {
        let recipe = this.getRecipe(param.id);
        if (recipe != null) {
            recipe.name = param.recipeDetails.recipeName;
            recipe.description = param.recipeDetails.recipeDescription;
            recipe.ingredients = param.recipeDetails.recipeIngredients;
            recipe.imagePath = param.recipeDetails.recipeImagePath;
        }
    }
}
